select substr(ic.claim_string,-18,10) claim_no,
icdi.claim_info_code , vds.due_name, nvl(icd.due_amount,0)- nvl(icd.salvage_amount,0) amount
from ins_claim ic, ins_assorted ia, ins_policy_types ipt, ins_claim_due_info icdi,
ins_claim_dues icd, v_due_setup vds
where ic.insurance_type = ia.assorted_code
and ic.claim_code =icdi.claim_code
and icdi.claim_info_code = icd.claim_info_code
and trim(icd.due_code) = trim(vds.due_code)
and ia.policy_type_code = ipt.policy_type_code
and ipt.class_code ='02'
